    } catch (error) {
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Resignation request routes
  app.get("/api/resignation-requests", async (req, res) => {
    try {
      const { status, classroomId } = req.query;

      let requests;
      if (status) {
