/* Definition of the design system. All colors, gradients, fonts, etc should be defined here. 
All colors MUST be HSL.
*/

@layer base {
  :root {
    /* HarmonyLearn Music-themed Design System */
    --background: 240 20% 98%;
    --foreground: 240 25% 12%;

    --card: 0 0% 100%;
    --card-foreground: 240 25% 12%;

    --popover: 0 0% 100%;
    --popover-foreground: 240 25% 12%;

    /* Deep Musical Blue */
    --primary: 220 85% 35%;
    --primary-foreground: 0 0% 98%;
    --primary-glow: 220 75% 50%;

    /* Warm Golden Accent */
    --secondary: 45 95% 55%;
    --secondary-foreground: 240 25% 12%;

    /* Soft Background Tones */
    --muted: 240 15% 96%;
    --muted-foreground: 240 15% 45%;

    /* Vibrant Music Accent */
    --accent: 280 75% 65%;
    --accent-foreground: 0 0% 98%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 98%;

    --border: 240 15% 88%;
    --input: 240 15% 92%;
    --ring: 220 85% 35%;

    /* Music-themed Gradients */
    --gradient-hero: linear-gradient(135deg, hsl(220 85% 35%), hsl(280 75% 65%));
    --gradient-warm: linear-gradient(135deg, hsl(45 95% 55%), hsl(25 85% 60%));
    --gradient-cool: linear-gradient(135deg, hsl(220 85% 35%), hsl(200 75% 45%));
    --gradient-subtle: linear-gradient(180deg, hsl(240 20% 98%), hsl(240 15% 96%));

    /* Musical Shadows */
    --shadow-musical: 0 10px 30px -10px hsl(220 85% 35% / 0.2);
    --shadow-glow: 0 0 40px hsl(220 75% 50% / 0.15);
    --shadow-warm: 0 8px 25px -8px hsl(45 95% 55% / 0.25);

    /* Smooth Musical Transitions */
    --transition-rhythm: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-beat: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);

    --radius: 0.5rem;

    --sidebar-background: 0 0% 98%;

    --sidebar-foreground: 240 5.3% 26.1%;

    --sidebar-primary: 240 5.9% 10%;

    --sidebar-primary-foreground: 0 0% 98%;

    --sidebar-accent: 240 4.8% 95.9%;

    --sidebar-accent-foreground: 240 5.9% 10%;

    --sidebar-border: 220 13% 91%;

    --sidebar-ring: 217.2 91.2% 59.8%;
  }

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;

    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;

    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;

    --primary: 210 40% 98%;
    --primary-foreground: 222.2 47.4% 11.2%;

    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;

    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;

    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;

    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;

    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 212.7 26.8% 83.9%;

    /* Dark theme gradients */
    --gradient-hero: linear-gradient(135deg, hsl(220 75% 50%), hsl(280 65% 55%));
    --gradient-warm: linear-gradient(135deg, hsl(45 85% 45%), hsl(25 75% 50%));
    --gradient-cool: linear-gradient(135deg, hsl(220 75% 50%), hsl(200 65% 40%));
    --gradient-subtle: linear-gradient(180deg, hsl(222.2 84% 4.9%), hsl(217.2 32.6% 17.5%));

    --sidebar-background: 240 5.9% 10%;
    --sidebar-foreground: 240 4.8% 95.9%;
    --sidebar-primary: 224.3 76.3% 48%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 3.7% 15.9%;
    --sidebar-accent-foreground: 240 4.8% 95.9%;
    --sidebar-border: 240 3.7% 15.9%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
  }
}

