                        <Badge className={getStatusColor(membership.status)}>
                          {getStatusText(membership.status)}
                        </Badge>
                      </div>
                      <div className="absolute bottom-4 left-4">
                        <h3 className="text-lg font-bold text-white">
                          {membership.subject}
