  coursesCompleted: number;
  totalStudyHours: number;
  averageProgress: number;
  certificatesEarned: number;
  currentStreak: number;
}

interface EnrolledCourse {
  id: number;
  title: string;
  instructor: string;
  progress: number;
  totalLessons: number;
  completedLessons: number;
