import { useState } from "react";
import { Star, MapPin, Clock, Video, Calendar, MessageCircle, Award, TrendingUp, UserCheck, Filter, Search, Send } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Form, FormControl, FormDescription, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";
// Create apiRequest function inline since it doesn't exist yet
const apiRequest = async (url: string, options: { method: string; body: any }) => {
  const response = await fetch(url, {
    method: options.method,
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(options.body),
  });

  if (!response.ok) {
    const errorData = await response.json().catch(() => ({}));
    console.error('API Error:', response.status, errorData);
    throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
  }

  return response.json();
};
// Define extended mentor type with user data
interface MentorWithUser {
  id: number;
  userId: number | null;
  firstName: string | null;
  lastName: string | null;
  specialization: string | null;
  experience: string | null;
  hourlyRate: string | null;
  location: string | null;
  languages: string[] | null;
  badges: any;
  bio: string | null;
  availability: any;
  totalStudents: number | null;
  totalReviews: number | null;
  averageRating: string | null;
  nextAvailableSession: any;
  isVerified: boolean | null;
  createdAt: Date | null;
}
import { EmptyState } from "@/components/EmptyState";
import { MentorCardSkeleton, LoadingGrid } from "@/components/LoadingSkeletons";

// Form schema for mentorship request
const mentorshipRequestSchema = z.object({
  message: z.string().min(10, "Please provide a detailed message (at least 10 characters)").max(500, "Message too long"),
});

type MentorshipRequestForm = z.infer<typeof mentorshipRequestSchema>;

export const StudentMentors = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedSpecialization, setSelectedSpecialization] = useState("all");
  const [sortBy, setSortBy] = useState("rating");
  const [selectedMentor, setSelectedMentor] = useState<MentorWithUser | null>(null);
  const [requestDialogOpen, setRequestDialogOpen] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get current user from localStorage
  const [currentUser] = useState(() => {
    const user = localStorage.getItem('currentUser');
    return user ? JSON.parse(user) : null;
  });

  const { data: mentors, isLoading, error } = useQuery<MentorWithUser[]>({
    queryKey: ['/api/mentors'],
  });

  // Form for mentorship request
  const form = useForm<MentorshipRequestForm>({
    resolver: zodResolver(mentorshipRequestSchema),
    defaultValues: {
      message: "",
    },
  });

  // Mutation for creating mentorship request
  const createRequestMutation = useMutation({
    mutationFn: async (data: { mentorId: number; studentId: number; message: string }) => {
      console.log('API Request data:', data);
      return apiRequest(`/api/mentorship-requests`, {
        method: "POST",
        body: data,
      });
    },
    onSuccess: () => {
      toast({
        title: "Request Sent!",
        description: "Your mentorship request has been sent successfully. The mentor will be notified.",
      });
      setRequestDialogOpen(false);
      form.reset();
      setSelectedMentor(null);
      // Optionally refetch mentorship requests
      queryClient.invalidateQueries({ queryKey: ['/api/mentorship-requests'] });
    },
    onError: (error: any) => {
      console.error('Mentorship request error:', error);
      toast({
        title: "Error",
        description: error.message || "Failed to send mentorship request. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Filter and sort mentors based on search and filters
  const filteredMentors = mentors?.filter(mentor => {
    const matchesSearch = mentor.bio?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         mentor.specialization?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesSpecialization = selectedSpecialization === "all" || 
                                 mentor.specialization?.toLowerCase().includes(selectedSpecialization.toLowerCase());
    return matchesSearch && matchesSpecialization;
  }).sort((a, b) => {
    if (sortBy === "rating") return (parseFloat(b.averageRating || "0") - parseFloat(a.averageRating || "0"));
    if (sortBy === "price") return (parseFloat(a.hourlyRate || "0") - parseFloat(b.hourlyRate || "0"));
    if (sortBy === "experience") return (b.totalStudents || 0) - (a.totalStudents || 0);
    return 0;
  });

  const specializations = ["Piano", "Guitar", "Violin", "Drums", "Voice", "Jazz", "Classical"];

  const handleRequestMentorship = (mentor: MentorWithUser) => {
    if (!currentUser) {
      toast({
        title: "Please Sign In",
        description: "You need to be signed in to request mentorship.",
        variant: "destructive",
      });
      return;
    }
    setSelectedMentor(mentor);
    setRequestDialogOpen(true);
  };

  const handleSendMessage = (mentorId: number) => {
    if (!currentUser) {
      toast({
        title: "Please Sign In",
        description: "You need to be signed in to message mentors.",
        variant: "destructive",
      });
      return;
    }
    // Navigate to mentor interactions page
    window.location.href = `/mentor-interactions`;
  };

  const onSubmitRequest = async (data: MentorshipRequestForm) => {
    if (!currentUser || !selectedMentor) {
      console.error('Missing user or mentor data');
      return;
    }

    if (!selectedMentor.userId) {
      console.error('Selected mentor missing userId');
      toast({
        title: "Error",
        description: "Invalid mentor data. Please try selecting the mentor again.",
        variant: "destructive",
      });
      return;
    }

    const requestData = {
      mentorId: selectedMentor.userId,
      studentId: typeof currentUser.id === 'string' ? parseInt(currentUser.id) : currentUser.id,
      message: data.message,
    };

    console.log('Submitting mentorship request:', {
      ...requestData,
      selectedMentor
    });

    createRequestMutation.mutate(requestData);
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2">Find Your Perfect Mentor</h1>
          <p className="text-muted-foreground">
            Loading expert musicians and teachers...
          </p>
        </div>
        <LoadingGrid count={6} className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          <MentorCardSkeleton />
        </LoadingGrid>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6">
        <div className="text-center py-20">
          <div className="text-lg text-red-600">Error loading mentors. Please try again later.</div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2">Find Your Perfect Mentor</h1>
        <p className="text-muted-foreground">
          Connect with expert musicians for personalized lessons and guidance
        </p>
      </div>

      {/* Search and Filters */}
      <Card className="mb-8">
        <CardContent className="p-6">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search by instrument, style, or expertise..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>

            <Select value={selectedSpecialization} onValueChange={setSelectedSpecialization}>
              <SelectTrigger className="w-48">
                <Filter className="h-4 w-4 mr-2" />
                <SelectValue placeholder="Specialization" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Instruments</SelectItem>
                {specializations.map(spec => (
                  <SelectItem key={spec} value={spec.toLowerCase()}>{spec}</SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Select value={sortBy} onValueChange={setSortBy}>
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Sort by" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="rating">Highest Rated</SelectItem>
                <SelectItem value="price">Lowest Price</SelectItem>
                <SelectItem value="experience">Most Experienced</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Mentors Grid */}
      {filteredMentors && filteredMentors.length > 0 ? (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredMentors.map((mentor) => (
            <Card key={mentor.id} className="group hover:shadow-musical transition-all duration-300 hover:-translate-y-1">
              <CardContent className="p-6">
                {/* Mentor Header */}
                <div className="flex items-start gap-4 mb-4">
                  <Avatar className="h-16 w-16">
                    <AvatarFallback className="text-lg font-semibold bg-gradient-hero text-white">
                      {mentor.specialization?.charAt(0) || "M"}
                    </AvatarFallback>
                  </Avatar>

                  <div className="flex-1">
                    <h3 className="text-xl font-semibold group-hover:text-primary transition-colors">
                      {mentor.firstName && mentor.lastName 
                        ? `${mentor.firstName} ${mentor.lastName}`
                        : `${mentor.specialization?.split(',')[0]?.trim() || 'Music'} Mentor`
                      }
                    </h3>
                    <div className="flex items-center gap-2 mt-1">
                      <Star className="h-4 w-4 fill-secondary text-secondary" />
                      <span className="font-medium">{mentor.averageRating || "4.8"}</span>
                      <span className="text-muted-foreground text-sm">
                        ({mentor.totalReviews || 0} reviews)
                      </span>
                    </div>
                    {mentor.location && (
                      <div className="flex items-center gap-1 mt-1 text-sm text-muted-foreground">
                        <MapPin className="h-3 w-3" />
                        {mentor.location}
                      </div>
                    )}
                  </div>
                </div>

                {/* Specialization Tags */}
                <div className="mb-4">
                  <div className="flex flex-wrap gap-1 mb-2">
                    {mentor.specialization?.split(',').map((spec, index) => (
                      <Badge key={index} variant="secondary" className="text-xs">
                        {spec.trim()}
                      </Badge>
                    ))}
                  </div>
                  <div className="flex items-center gap-2 text-sm text-muted-foreground">
                    <TrendingUp className="h-4 w-4" />
                    {mentor.totalStudents || 0} students taught
                  </div>
                </div>

                {/* Bio */}
                {mentor.bio && (
                  <p className="text-sm text-muted-foreground mb-4 line-clamp-3">
                    {mentor.bio}
                  </p>
                )}

                {/* Experience & Languages */}
                <div className="space-y-2 mb-4 text-sm">
                  {mentor.experience && (
                    <div className="flex items-center gap-2">
                      <Award className="h-4 w-4 text-primary" />
                      <span>{mentor.experience}</span>
                    </div>
                  )}
                  {mentor.languages && mentor.languages.length > 0 && (
                    <div className="flex items-center gap-2">
                      <MessageCircle className="h-4 w-4 text-primary" />
                      <span>Speaks: {mentor.languages.join(', ')}</span>
                    </div>
                  )}
                </div>

                {/* Pricing & Actions */}
                <div className="flex items-center justify-between pt-4 border-t">
                  <div className="text-lg font-bold text-primary">
                    ${mentor.hourlyRate || 50}/hour
                  </div>
                  <div className="flex gap-2">
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => handleSendMessage(mentor.id)}
                    >
                      <MessageCircle className="h-4 w-4" />
                    </Button>
                    <Button 
                      size="sm" 
                      className="shadow-musical"
                      onClick={() => handleRequestMentorship(mentor)}
                    >
                      <Send className="mr-2 h-4 w-4" />
                      Request
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      ) : (
        <EmptyState
          icon={UserCheck}
          title="No Mentors Found"
          description="Try adjusting your search terms or filters to find the perfect mentor for your musical journey."
          actionText="Clear Filters"
          onAction={() => {
            setSearchTerm("");
            setSelectedSpecialization("all");
            setSortBy("rating");
          }}
        />
      )}

      {/* Stats Section */}
      {filteredMentors && filteredMentors.length > 0 && (
        <div className="mt-12 p-6 bg-muted/30 rounded-xl">
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6 text-center">
            <div>
              <div className="text-2xl font-bold text-primary">{filteredMentors.length}</div>
              <div className="text-sm text-muted-foreground">Expert Mentors</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-primary">
                {filteredMentors.reduce((sum, mentor) => sum + (mentor.totalStudents || 0), 0)}
              </div>
              <div className="text-sm text-muted-foreground">Students Taught</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-primary">
                {(filteredMentors.reduce((sum, mentor) => sum + parseFloat(mentor.averageRating || "0"), 0) / filteredMentors.length).toFixed(1)}
              </div>
              <div className="text-sm text-muted-foreground">Average Rating</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-primary">24/7</div>
              <div className="text-sm text-muted-foreground">Support Available</div>
            </div>
          </div>
        </div>
      )}

      {/* Mentorship Request Dialog */}
      <Dialog open={requestDialogOpen} onOpenChange={setRequestDialogOpen}>
        <DialogContent className="sm:max-w-[425px]">
          <DialogHeader>
            <DialogTitle>Request Mentorship</DialogTitle>
            <DialogDescription>
              Send a mentorship request to {selectedMentor?.firstName && selectedMentor?.lastName 
                ? `${selectedMentor.firstName} ${selectedMentor.lastName}`
                : 'this mentor'
              }. Tell them about your musical goals and why you'd like their guidance.
            </DialogDescription>
          </DialogHeader>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmitRequest)} className="space-y-4">
              <FormField
                control={form.control}
                name="message"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Your Message</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="Hi! I'm interested in learning [instrument/skill] and would love to have you as my mentor. My current level is... My goals are..."
                        className="min-h-[120px]"
                        {...field}
                      />
                    </FormControl>
                    <FormDescription>
                      Introduce yourself and explain what you hope to achieve through mentorship.
                    </FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <DialogFooter>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setRequestDialogOpen(false)}
                >
                  Cancel
                </Button>
                <Button 
                  type="submit" 
                  disabled={createRequestMutation.isPending}
                  className="shadow-musical"
                >
                  {createRequestMutation.isPending ? "Sending..." : "Send Request"}
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default StudentMentors;

client/src/pages/StudentSessions.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Footer } from "@/components/Footer";
import { StudentNavigation } from "@/components/student/StudentNavigation";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { 
  Video, 
  Calendar, 
  Clock, 
  Users,
  Plus,
  Play,
  User,
  BookOpen,
  MessageCircle,
  Star,
  Phone,
  Settings
} from "lucide-react";

interface LiveSession {
  id: number;
  title: string;
  instructor: string;
  instructorAvatar?: string;
  date: string;
  time: string;
  duration: number;
  type: 'one-on-one' | 'group' | 'masterclass';
  status: 'scheduled' | 'ongoing' | 'completed' | 'cancelled';
  participantCount?: number;
  maxParticipants?: number;
  description: string;
  meetingLink?: string;
  category: string;
}

const StudentSessions = () => {
  const [currentUser] = useState(() => {
    const user = localStorage.getItem('currentUser');
    return user ? JSON.parse(user) : null;
  });

  // Fetch student's live sessions
  const { data: sessions = [] } = useQuery<any[]>({
    queryKey: ['/api/live-sessions', { student: currentUser?.id }],
    enabled: !!currentUser?.id
  });

  // Transform API data to LiveSession interface
  const liveSessions: LiveSession[] = sessions.map((session: any) => ({
    id: session.id,
    title: session.title,
    instructor: session.mentorName || 'Instructor',
    instructorAvatar: session.mentorAvatar || '',
    date: new Date(session.scheduledDate).toLocaleDateString(),
    time: new Date(session.scheduledDate).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
    duration: session.durationMinutes || 60,
    type: session.type || 'one-on-one',
    status: session.status,
    participantCount: session.participantCount || 1,
    maxParticipants: session.maxParticipants || 1,
    description: session.description || 'Live learning session',
    meetingLink: session.meetingLink,
    category: session.category || 'Music'
  }));

  // Separate sessions by status
  const upcomingSessions = liveSessions.filter(s => s.status === 'scheduled');
  const ongoingSessions = liveSessions.filter(s => s.status === 'ongoing');
  const completedSessions = liveSessions.filter(s => s.status === 'completed');

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'scheduled': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300';
      case 'ongoing': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300';
      case 'completed': return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300';
      case 'cancelled': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300';
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'one-on-one': return <User className="h-4 w-4" />;
      case 'group': return <Users className="h-4 w-4" />;
      case 'masterclass': return <BookOpen className="h-4 w-4" />;
      default: return <Video className="h-4 w-4" />;
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'one-on-one': return 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-300';
      case 'group': return 'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-300';
      case 'masterclass': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300';
    }
  };

  const SessionCard = ({ session }: { session: LiveSession }) => (
    <Card className="overflow-hidden hover:shadow-lg transition-shadow">
      <CardContent className="p-6">
        <div className="flex items-start justify-between mb-4">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-blue-100 dark:bg-blue-900">
              <Video className="h-5 w-5 text-blue-600" />
            </div>
            <div>
              <h3 className="font-semibold">{session.title}</h3>
              <p className="text-sm text-muted-foreground">with {session.instructor}</p>
            </div>
          </div>
          <div className="flex gap-2">
            <Badge variant="outline" className={getStatusColor(session.status)}>
              {session.status}
            </Badge>
            <Badge variant="outline" className={getTypeColor(session.type)}>
              <span className="flex items-center gap-1">
                {getTypeIcon(session.type)}
                {session.type}
              </span>
            </Badge>
          </div>
        </div>

        <p className="text-sm text-muted-foreground mb-4">{session.description}</p>

        <div className="grid grid-cols-2 gap-4 mb-4">
          <div className="flex items-center gap-2 text-sm">
            <Calendar className="h-4 w-4 text-muted-foreground" />
            <span>{session.date}</span>
          </div>
          <div className="flex items-center gap-2 text-sm">
            <Clock className="h-4 w-4 text-muted-foreground" />
            <span>{session.time} ({session.duration}min)</span>
          </div>
        </div>

        {session.type !== 'one-on-one' && (
          <div className="flex items-center gap-2 text-sm mb-4">
            <Users className="h-4 w-4 text-muted-foreground" />
            <span>{session.participantCount}/{session.maxParticipants} participants</span>
          </div>
        )}

        <div className="flex items-center gap-2">
          {session.status === 'ongoing' && (
            <Button className="flex-1 gap-2">
              <Play className="h-4 w-4" />
              Join Now
            </Button>
          )}
          {session.status === 'scheduled' && (
            <>
              <Button className="flex-1 gap-2">
                <Calendar className="h-4 w-4" />
                Join Session
              </Button>
              <Button variant="outline" size="sm">
                <MessageCircle className="h-4 w-4" />
              </Button>
            </>
          )}
          {session.status === 'completed' && (
            <Button variant="outline" className="flex-1 gap-2">
              <Play className="h-4 w-4" />
              Watch Recording
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );

  return (
    <div className="min-h-screen bg-background flex">
      {/* Sidebar Navigation */}
      <div className="hidden lg:block w-64 flex-shrink-0">
        <div className="sticky top-0 h-screen">
          <StudentNavigation currentUser={currentUser} className="h-full" />
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 min-w-0">

      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold mb-2">Live Sessions</h1>
            <p className="text-muted-foreground">
              Join live learning sessions with expert instructors
            </p>
          </div>
          <Button className="gap-2">
            <Plus className="h-4 w-4" />
            Book Session
          </Button>
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-2xl font-bold">{upcomingSessions.length}</p>
                  <p className="text-xs text-muted-foreground">Upcoming</p>
                </div>
                <Calendar className="h-8 w-8 text-blue-500" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-2xl font-bold">{ongoingSessions.length}</p>
                  <p className="text-xs text-muted-foreground">Live Now</p>
                </div>
                <Video className="h-8 w-8 text-green-500" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-2xl font-bold">{completedSessions.length}</p>
                  <p className="text-xs text-muted-foreground">Completed</p>
                </div>
                <Users className="h-8 w-8 text-purple-500" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-2xl font-bold">{liveSessions.length}</p>
                  <p className="text-xs text-muted-foreground">Total Sessions</p>
                </div>
                <BookOpen className="h-8 w-8 text-orange-500" />
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Sessions Tabs */}
        <Tabs defaultValue="upcoming" className="space-y-6">
          <TabsList>
            <TabsTrigger value="upcoming" className="gap-2">
              <Calendar className="h-4 w-4" />
              Upcoming ({upcomingSessions.length})
            </TabsTrigger>
            <TabsTrigger value="live" className="gap-2">
              <Video className="h-4 w-4" />
              Live Now ({ongoingSessions.length})
            </TabsTrigger>
            <TabsTrigger value="completed" className="gap-2">
              <Users className="h-4 w-4" />
              Completed ({completedSessions.length})
            </TabsTrigger>
          </TabsList>

          {/* Upcoming Sessions */}
          <TabsContent value="upcoming">
            {upcomingSessions.length === 0 ? (
              <Card>
                <CardContent className="text-center py-12">
                  <Calendar className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                  <h3 className="text-lg font-semibold mb-2">No upcoming sessions</h3>
                  <p className="text-muted-foreground mb-4">
                    Book a live session with your instructor to accelerate your learning!
                  </p>
                  <Button>Book a Session</Button>
                </CardContent>
              </Card>
            ) : (
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {upcomingSessions.map((session) => (
                  <SessionCard key={session.id} session={session} />
                ))}
              </div>
            )}
          </TabsContent>

          {/* Live Sessions */}
          <TabsContent value="live">
            {ongoingSessions.length === 0 ? (
              <Card>
                <CardContent className="text-center py-12">
                  <Video className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                  <h3 className="text-lg font-semibold mb-2">No live sessions</h3>
                  <p className="text-muted-foreground">
                    No sessions are currently live. Check back for upcoming sessions!
                  </p>
                </CardContent>
              </Card>
            ) : (
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {ongoingSessions.map((session) => (
                  <SessionCard key={session.id} session={session} />
                ))}
              </div>
            )}
          </TabsContent>

          {/* Completed Sessions */}
          <TabsContent value="completed">
            {completedSessions.length === 0 ? (
              <Card>
                <CardContent className="text-center py-12">
                  <Users className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                  <h3 className="text-lg font-semibold mb-2">No completed sessions</h3>
                  <p className="text-muted-foreground">
                    Your completed sessions and recordings will appear here.
                  </p>
                </CardContent>
              </Card>
            ) : (
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {completedSessions.map((session) => (
                  <SessionCard key={session.id} session={session} />
                ))}
              </div>
            )}
          </TabsContent>
        </Tabs>

        {/* Quick Actions */}
        <Card className="mt-8">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Settings className="h-5 w-5" />
              Session Preferences
            </CardTitle>
            <CardDescription>
              Manage your session settings and availability
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <Button variant="outline" className="gap-2">
                <Calendar className="h-4 w-4" />
                Set Availability
              </Button>
              <Button variant="outline" className="gap-2">
                <Phone className="h-4 w-4" />
                Test Audio/Video
              </Button>
              <Button variant="outline" className="gap-2">
                <Star className="h-4 w-4" />
                Rate Instructors
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>

        <Footer />
      </div>
    </div>
  );
};

export default StudentSessions;

client/src/pages/StudentSignIn.tsx.content:
import { useState } from "react";
import { useLocation, Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { Header } from "@/components/Header";
import { Footer } from "@/components/Footer";
import { BookOpen, Music, Users, Loader2 } from "lucide-react";

const StudentSignIn = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const handleSignIn = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        // Check if user is actually a student
        if (data.user.role !== 'student') {
          toast({
            title: "Access Denied",
            description: "This is the student portal. Please use the mentor portal to sign in.",
            variant: "destructive",
          });
          return;
        }

        toast({
          title: "Welcome Student!",
          description: "Logged in successfully. Start your musical journey!",
        });

        // Store student session data using centralized auth utility
        const userData = {
          id: data.user.id.toString(),
          username: data.user.username,
          firstName: data.user.firstName,
          lastName: data.user.lastName,
          email: data.user.email,
          role: data.user.role as 'student'
        };

        // Store in centralized auth system
        localStorage.setItem('currentUser', JSON.stringify(userData));

        // Dispatch login event for other components
        window.dispatchEvent(new CustomEvent('user-login', { detail: userData }));

        // Redirect to student dashboard
        setLocation("/student-dashboard");
      } else {
        toast({
          title: "Sign In Failed",
          description: data.error || "Please check your email and password.",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Connection Error",
        description: "Unable to connect to the server. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleSocialLogin = (provider: string) => {
    toast({
      title: `${provider} Login`,
      description: `${provider} authentication would be integrated here.`,
    });
  };

  return (
    <div className="min-h-screen bg-gradient-subtle">
      <Header />
      <div className="container mx-auto px-4 py-16 flex items-center justify-center">
        <div className="w-full max-w-md">
          {/* Student-specific branding */}
          <div className="text-center mb-8 animate-slide-up">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-hero rounded-full mb-4 shadow-glow">
              <BookOpen className="w-8 h-8 text-primary-foreground" />
            </div>
            <h1 className="text-3xl font-bold bg-gradient-hero bg-clip-text text-transparent">
              Student Portal
            </h1>
            <p className="text-muted-foreground mt-2">Begin your musical learning adventure</p>
          </div>

          <Card className="shadow-musical border-0 bg-card/80 backdrop-blur-sm animate-slide-up">
            <CardHeader className="space-y-1 pb-4">
              <CardTitle className="text-2xl text-center font-semibold">Welcome Back</CardTitle>
              <CardDescription className="text-center">
                Sign in to continue your musical journey
              </CardDescription>
            </CardHeader>

            <CardContent className="space-y-6">
              {/* Social Login Buttons */}
              <div className="grid grid-cols-2 gap-3">
                <Button 
                  variant="outline" 
                  className="transition-rhythm hover:shadow-warm"
                  onClick={() => handleSocialLogin("Google")}
                >
                  <svg className="w-4 h-4 mr-2" viewBox="0 0 24 24">
                    <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                    <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                    <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                    <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                  </svg>
                  Google
                </Button>
                <Button 
                  variant="outline" 
                  className="transition-rhythm hover:shadow-warm"
                  onClick={() => handleSocialLogin("Facebook")}
                >
                  <svg className="w-4 h-4 mr-2" viewBox="0 0 24 24">
                    <path fill="currentColor" d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
                  </svg>
                  Facebook
                </Button>
              </div>

              <div className="flex items-center gap-4">
                <Separator className="flex-1" />
                <span className="text-muted-foreground text-sm">or continue with email</span>
                <Separator className="flex-1" />
              </div>

              {/* Email/Password Form */}
              <form onSubmit={handleSignIn} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="email" className="text-sm font-medium">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    placeholder="student@example.com"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="transition-rhythm focus:shadow-glow"
                    required
                  />
                </div>
                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <Label htmlFor="password" className="text-sm font-medium">Password</Label>
                    <Link 
                      to="#" 
                      className="text-sm text-primary hover:text-primary-glow transition-beat"
                    >
                      Forgot password?
                    </Link>
                  </div>
                  <Input
                    id="password"
                    type="password"
                    placeholder="Enter your password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="transition-rhythm focus:shadow-glow"
                    required
                  />
                </div>

                <Button 
                  type="submit" 
                  disabled={isSubmitting}
                  className="w-full bg-gradient-hero hover:opacity-90 transition-rhythm shadow-musical font-medium"
                >
                  {isSubmitting ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Signing In...
                    </>
                  ) : (
                    <>
                      <Music className="w-4 h-4 mr-2" />
                      Sign In to Learn
                    </>
                  )}
                </Button>
              </form>

              {/* Additional Options */}
              <div className="space-y-4">
                <div className="flex items-center gap-3">
                  <Button 
