import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { getCurrentUser } from "@/lib/auth";
import { Crown, Eye, CheckCircle, XCircle, Clock, User } from "lucide-react";
import { format } from "date-fns";

const apiRequest = async (url: string, options: { method: string; body?: string }) => {
  const response = await fetch(url, {
    method: options.method,
    headers: {
      'Content-Type': 'application/json',
    },
    body: options.body,
  });

  if (!response.ok) {
    const errorData = await response.json().catch(() => ({}));
    throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
  }

  return response.json();
};

interface MasterRoleRequest {
  id: number;
  mentorId: number;
  reason: string;
  experience: string;
  plannedClassrooms: string;
  additionalQualifications?: string;
  status: 'pending' | 'approved' | 'rejected';
  adminNotes?: string;
  reviewedBy?: number;
  reviewedAt?: Date;
  approvedAt?: Date;
  rejectedAt?: Date;
  createdAt: Date;
}

const reviewSchema = z.object({
  status: z.enum(['approved', 'rejected']),
  adminNotes: z.string().optional(),
});

type ReviewFormData = z.infer<typeof reviewSchema>;

const getStatusColor = (status: string) => {
  switch (status) {
    case 'pending':
      return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300';
    case 'approved':
      return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300';
    case 'rejected':
      return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300';
    default:
      return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300';
  }
};

const getStatusIcon = (status: string) => {
  switch (status) {
    case 'pending':
      return <Clock className="w-4 h-4" />;
    case 'approved':
      return <CheckCircle className="w-4 h-4" />;
    case 'rejected':
      return <XCircle className="w-4 h-4" />;
    default:
      return <User className="w-4 h-4" />;
  }
};

interface RequestDetailsDialogProps {
  request: MasterRoleRequest;
  onReview: (requestId: number, data: ReviewFormData) => void;
  isReviewing: boolean;
}

function RequestDetailsDialog({ request, onReview, isReviewing }: RequestDetailsDialogProps) {
  const [open, setOpen] = useState(false);

  const form = useForm<ReviewFormData>({
    resolver: zodResolver(reviewSchema),
    defaultValues: {
      adminNotes: "",
    },
  });

  const onSubmit = (data: ReviewFormData) => {
    onReview(request.id, data);
    setOpen(false);
    form.reset();
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button variant="outline" size="sm">
          <Eye className="w-4 h-4 mr-2" />
          Review
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-3xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Crown className="w-5 h-5 text-yellow-500" />
            Master Role Request Details
          </DialogTitle>
          <DialogDescription>
            Review and approve or reject this master role request
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Request Details */}
          <div className="space-y-4">
            <div className="flex items-center gap-2">
              <Badge className={getStatusColor(request.status)}>
                {request.status.charAt(0).toUpperCase() + request.status.slice(1)}
              </Badge>
              <span className="text-sm text-muted-foreground">
                Submitted: {format(new Date(request.createdAt), "MMM d, yyyy 'at' h:mm a")}
              </span>
            </div>

            <div className="grid gap-4">
              <div>
                <h4 className="font-semibold mb-2">Why they want to become a master:</h4>
                <p className="text-sm bg-muted p-3 rounded-lg">{request.reason}</p>
              </div>

              <div>
                <h4 className="font-semibold mb-2">Teaching & Classroom Management Experience:</h4>
                <p className="text-sm bg-muted p-3 rounded-lg">{request.experience}</p>
              </div>

              <div>
                <h4 className="font-semibold mb-2">Planned Classrooms & Teaching Approach:</h4>
                <p className="text-sm bg-muted p-3 rounded-lg">{request.plannedClassrooms}</p>
              </div>

              {request.additionalQualifications && (
                <div>
                  <h4 className="font-semibold mb-2">Additional Qualifications:</h4>
                  <p className="text-sm bg-muted p-3 rounded-lg">{request.additionalQualifications}</p>
                </div>
              )}
            </div>
          </div>

          {/* Review Form - Only show if pending */}
          {request.status === 'pending' && (
            <div className="border-t pt-6">
              <h4 className="font-semibold mb-4">Review Decision</h4>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="status"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Decision</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue placeholder="Select decision" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="approved">Approve Request</SelectItem>
                            <SelectItem value="rejected">Reject Request</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="adminNotes"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Admin Notes (Optional)</FormLabel>
                        <FormControl>
                          <Textarea
                            placeholder="Add notes about your decision..."
                            className="min-h-[100px]"
                            {...field}
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="flex gap-2">
                    <Button
                      type="submit"
                      disabled={isReviewing || !form.watch('status')}
                    >
                      {isReviewing ? "Processing..." : "Submit Review"}
                    </Button>
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => setOpen(false)}
                    >
                      Cancel
                    </Button>
                  </div>
                </form>
              </Form>
            </div>
          )}

          {/* Previous Review Info */}
          {request.status !== 'pending' && (
            <div className="border-t pt-6">
              <h4 className="font-semibold mb-2">Review Information</h4>
              <div className="text-sm space-y-1">
                <p><strong>Status:</strong> {request.status.charAt(0).toUpperCase() + request.status.slice(1)}</p>
                {request.reviewedAt && (
                  <p><strong>Reviewed:</strong> {format(new Date(request.reviewedAt), "MMM d, yyyy 'at' h:mm a")}</p>
                )}
                {request.adminNotes && (
                  <div className="mt-2">
                    <strong>Admin Notes:</strong>
                    <p className="bg-muted p-2 rounded mt-1">{request.adminNotes}</p>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

export default function MasterRoleRequestManager() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const currentUser = getCurrentUser();
  const [statusFilter, setStatusFilter] = useState<string>('all');

  const { data: requests, isLoading } = useQuery({
    queryKey: ['/api/master-role-requests', statusFilter],
    queryFn: async () => {
      const url = statusFilter === 'all' 
        ? '/api/master-role-requests' 
        : `/api/master-role-requests?status=${statusFilter}`;
      return fetch(url).then(res => res.json());
    },
  });

  // Fetch all mentors to get names
  const { data: mentors } = useQuery({
    queryKey: ['/api/users', 'mentors'],
    queryFn: async () => {
      return fetch('/api/users?role=mentor').then(res => res.json());
    },
  });

  const getMentorName = (mentorId: number) => {
    const mentor = mentors?.find((m: any) => m.id === mentorId);
    return mentor ? `${mentor.firstName || ''} ${mentor.lastName || ''}`.trim() || mentor.username : `Mentor ${mentorId}`;
  };

  const reviewRequest = useMutation({
    mutationFn: async ({ requestId, data }: { requestId: number; data: ReviewFormData }) => {
      return apiRequest(`/api/master-role-requests/${requestId}/status`, {
        method: 'PATCH',
        body: JSON.stringify({
          ...data,
          reviewedBy: parseInt(currentUser?.id || '0'),
        }),
      });
    },
    onSuccess: () => {
      toast({
        title: "Request Reviewed",
        description: "The master role request has been reviewed successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/master-role-requests'] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to review request",
        variant: "destructive",
      });
    },
  });

  const handleReview = (requestId: number, data: ReviewFormData) => {
    reviewRequest.mutate({ requestId, data });
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="pt-6">
          <div className="animate-pulse space-y-4">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="h-16 bg-gray-200 dark:bg-gray-700 rounded"></div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  const requestList = Array.isArray(requests) ? requests as MasterRoleRequest[] : [];

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Crown className="w-5 h-5 text-yellow-500" />
          Master Role Requests
        </CardTitle>
        <CardDescription>
          Review and approve mentor applications for master status
        </CardDescription>

        <div className="flex gap-2">
          <Select value={statusFilter} onValueChange={setStatusFilter}>
            <SelectTrigger className="w-48">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Requests</SelectItem>
              <SelectItem value="pending">Pending</SelectItem>
              <SelectItem value="approved">Approved</SelectItem>
              <SelectItem value="rejected">Rejected</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </CardHeader>

      <CardContent>
        {requestList.length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            No master role requests found.
          </div>
        ) : (
          <div className="space-y-4">
            {requestList.map((request) => (
              <div key={request.id} className="border rounded-lg p-4 space-y-3">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    {getStatusIcon(request.status)}
                    <div>
                      <p className="font-medium">{getMentorName(request.mentorId)}</p>
                      <p className="text-sm text-muted-foreground">
                        Submitted {format(new Date(request.createdAt), "MMM d, yyyy")}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    <Badge className={getStatusColor(request.status)}>
                      {request.status.charAt(0).toUpperCase() + request.status.slice(1)}
                    </Badge>
                    <RequestDetailsDialog
                      request={request}
                      onReview={handleReview}
                      isReviewing={reviewRequest.isPending}
                    />
                  </div>
                </div>

                <div className="text-sm">
                  <p className="line-clamp-2">{request.reason}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/admin/MentorApplic.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { 
  GraduationCap, Search, Filter, Eye, CheckCircle, X, Clock, 
  Award, User, Calendar, FileText, MessageSquare
} from "lucide-react";

interface MentorApplication {
  id: number;
  userId: number;
  name: string;
  email: string;
  specialization: string;
  experience: string;
  bio: string;
  credentials: string;
  portfolio?: string;
  status: 'pending' | 'approved' | 'rejected';
  adminNotes?: string;
  reviewedBy?: number;
  reviewedAt?: string;
  createdAt: string;
}

export const MentorApplicationManagement = () => {
  const { toast } = useToast();
  const [applications, setApplications] = useState<MentorApplication[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [selectedApplication, setSelectedApplication] = useState<MentorApplication | null>(null);
  const [reviewNotes, setReviewNotes] = useState("");
  const [isReviewing, setIsReviewing] = useState(false);

  const fetchApplications = async () => {
    try {
      const response = await fetch('/api/mentor-applications');
      if (response.ok) {
        const data = await response.json();
        setApplications(data);
      } else {
        toast({
          title: "Error",
          description: "Failed to fetch mentor applications",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Connection Error",
        description: "Unable to fetch applications",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchApplications();
  }, []);

  const filteredApplications = applications.filter(app => {
    const matchesSearch = app.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         app.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         app.specialization.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === "all" || app.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  const handleReviewApplication = async (applicationId: number, status: 'approved' | 'rejected') => {
    setIsReviewing(true);

    try {
      const response = await fetch(`/api/mentor-applications/${applicationId}/status`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          status,
          adminNotes: reviewNotes,
          reviewedBy: 1 // TODO: Get actual admin user ID
        }),
      });

      if (response.ok) {
        toast({
          title: "Application Updated",
          description: `Application ${status} successfully`,
        });

        // Refresh applications
        await fetchApplications();
        setSelectedApplication(null);
        setReviewNotes("");
      } else {
        const data = await response.json();
        toast({
          title: "Update Failed",
          description: data.error || "Failed to update application",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Connection Error",
        description: "Unable to update application",
        variant: "destructive",
      });
    } finally {
      setIsReviewing(false);
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge variant="outline" className="bg-yellow-50 text-yellow-700 border-yellow-200"><Clock className="w-3 h-3 mr-1" />Pending</Badge>;
      case 'approved':
        return <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200"><CheckCircle className="w-3 h-3 mr-1" />Approved</Badge>;
      case 'rejected':
        return <Badge variant="outline" className="bg-red-50 text-red-700 border-red-200"><X className="w-3 h-3 mr-1" />Rejected</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center gap-3">
          <GraduationCap className="w-6 h-6 text-secondary" />
          <h2 className="text-2xl font-bold">Mentor Applications</h2>
        </div>
        <Card>
          <CardContent className="flex items-center justify-center py-12">
            <div className="text-center">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
              <p className="text-muted-foreground mt-2">Loading applications...</p>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <GraduationCap className="w-6 h-6 text-secondary" />
          <h2 className="text-2xl font-bold">Mentor Applications</h2>
          <Badge variant="secondary">{applications.length} Total</Badge>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="flex items-center p-6">
            <Clock className="w-8 h-8 text-yellow-500 mr-3" />
            <div>
              <p className="text-2xl font-bold">{applications.filter(a => a.status === 'pending').length}</p>
              <p className="text-xs text-muted-foreground">Pending Review</p>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="flex items-center p-6">
            <CheckCircle className="w-8 h-8 text-green-500 mr-3" />
            <div>
              <p className="text-2xl font-bold">{applications.filter(a => a.status === 'approved').length}</p>
              <p className="text-xs text-muted-foreground">Approved</p>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="flex items-center p-6">
            <X className="w-8 h-8 text-red-500 mr-3" />
            <div>
              <p className="text-2xl font-bold">{applications.filter(a => a.status === 'rejected').length}</p>
              <p className="text-xs text-muted-foreground">Rejected</p>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="flex items-center p-6">
            <Award className="w-8 h-8 text-blue-500 mr-3" />
            <div>
              <p className="text-2xl font-bold">{applications.length}</p>
              <p className="text-xs text-muted-foreground">Total Applications</p>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-6">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <Input
                  placeholder="Search applications..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-48">
                <Filter className="w-4 h-4 mr-2" />
                <SelectValue placeholder="Filter by status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
                <SelectItem value="approved">Approved</SelectItem>
                <SelectItem value="rejected">Rejected</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Applications Table */}
      <Card>
        <CardHeader>
          <CardTitle>Applications ({filteredApplications.length})</CardTitle>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Applicant</TableHead>
                <TableHead>Specialization</TableHead>
                <TableHead>Experience</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Applied</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredApplications.map((application) => (
                <TableRow key={application.id}>
                  <TableCell>
                    <div className="flex items-center space-x-3">
                      <User className="w-8 h-8 text-gray-400" />
                      <div>
                        <div className="font-medium">{application.name || 'Unknown User'}</div>
                        <div className="text-sm text-muted-foreground">{application.email || 'No email'}</div>
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>{application.specialization}</TableCell>
                  <TableCell className="max-w-xs truncate">{application.experience}</TableCell>
                  <TableCell>{getStatusBadge(application.status)}</TableCell>
                  <TableCell className="text-sm text-muted-foreground">
                    <div className="flex items-center">
                      <Calendar className="w-4 h-4 mr-1" />
                      {formatDate(application.createdAt)}
                    </div>
                  </TableCell>
                  <TableCell>
                    <Dialog>
                      <DialogTrigger asChild>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => setSelectedApplication(application)}
                        >
                          <Eye className="w-4 h-4 mr-1" />
                          Review
                        </Button>
                      </DialogTrigger>
                      <DialogContent className="max-w-2xl">
                        <DialogHeader>
                          <DialogTitle>Review Mentor Application</DialogTitle>
                        </DialogHeader>
                        {selectedApplication && (
                          <div className="space-y-6">
                            {/* Applicant Info */}
                            <div className="grid grid-cols-2 gap-4">
                              <div>
                                <Label className="text-sm font-medium">Applicant Name</Label>
                                <p className="mt-1">{selectedApplication.name || 'Unknown User'}</p>
                              </div>
                              <div>
                                <Label className="text-sm font-medium">Email</Label>
                                <p className="mt-1">{selectedApplication.email || 'No email'}</p>
                              </div>
                            </div>

                            {/* Application Details */}
                            <div className="space-y-4">
                              <div>
                                <Label className="text-sm font-medium">Specialization</Label>
                                <p className="mt-1">{selectedApplication.specialization}</p>
                              </div>
                              <div>
                                <Label className="text-sm font-medium">Experience</Label>
                                <p className="mt-1">{selectedApplication.experience}</p>
                              </div>
                              <div>
                                <Label className="text-sm font-medium">Bio</Label>
                                <p className="mt-1">{selectedApplication.bio}</p>
                              </div>
                              <div>
                                <Label className="text-sm font-medium">Credentials</Label>
                                <p className="mt-1">{selectedApplication.credentials}</p>
                              </div>
                            </div>

                            {/* Review Notes */}
                            <div>
                              <Label htmlFor="reviewNotes" className="text-sm font-medium">Admin Notes</Label>
                              <Textarea
                                id="reviewNotes"
                                placeholder="Add notes about this application..."
                                value={reviewNotes}
                                onChange={(e) => setReviewNotes(e.target.value)}
                                className="mt-1"
                                rows={3}
                              />
                            </div>

                            {/* Action Buttons */}
                            {selectedApplication.status === 'pending' && (
                              <div className="flex gap-3 pt-4">
                                <Button
                                  onClick={() => handleReviewApplication(selectedApplication.id, 'approved')}
                                  disabled={isReviewing}
                                  className="bg-green-600 hover:bg-green-700"
                                >
                                  <CheckCircle className="w-4 h-4 mr-2" />
                                  Approve Application
                                </Button>
                                <Button
                                  variant="destructive"
                                  onClick={() => handleReviewApplication(selectedApplication.id, 'rejected')}
                                  disabled={isReviewing}
                                >
                                  <X className="w-4 h-4 mr-2" />
                                  Reject Application
                                </Button>
                              </div>
                            )}

                            {/* Existing Review Info */}
                            {selectedApplication.status !== 'pending' && (
                              <div className="bg-gray-50 p-4 rounded-lg">
                                <div className="flex items-center gap-2 mb-2">
                                  <MessageSquare className="w-4 h-4" />
                                  <span className="font-medium">Review Status: {getStatusBadge(selectedApplication.status)}</span>
                                </div>
                                {selectedApplication.adminNotes && (
                                  <div>
                                    <p className="text-sm font-medium">Admin Notes:</p>
                                    <p className="text-sm text-muted-foreground mt-1">{selectedApplication.adminNotes}</p>
                                  </div>
                                )}
                                {selectedApplication.reviewedAt && (
                                  <p className="text-xs text-muted-foreground mt-2">
                                    Reviewed on {formatDate(selectedApplication.reviewedAt)}
                                  </p>
                                )}
                              </div>
                            )}
                          </div>
                        )}
                      </DialogContent>
                    </Dialog>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>

          {filteredApplications.length === 0 && (
            <div className="text-center py-12">
              <GraduationCap className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No applications found</h3>
              <p className="text-gray-500">No mentor applications match your current filters.</p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

client/src/components/classroom/AcademyC.content:
import { useState, useEffect } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  Select, 
  SelectContent, 
  SelectItem, 
  SelectTrigger, 
  SelectValue 
} from "@/components/ui/select";
import { 
  Music, 
  Plus, 
  X, 
  Palette, 
  Globe, 
  MapPin,
  Phone,
  Mail,
  Star,
  DollarSign,
  Calendar,
  Image,
  Settings
} from "lucide-react";
import { getCurrentUser } from "@/lib/auth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { insertClassroomSchema } from "@shared/schema";

// Create a simplified schema for frontend form validation
const academyCreationSchema = z.object({
  academyName: z.string().min(3, "Academy name must be at least 3 characters"),
  description: z.string().min(10, "Description must be at least 10 characters"),
  about: z.string().min(20, "About section must be at least 20 characters"),
  curriculum: z.string().min(20, "Curriculum must be at least 20 characters"),
  customSlug: z.string()
    .min(3, "URL slug must be at least 3 characters")
    .regex(/^[a-z0-9-]+$/, "URL slug can only contain lowercase letters, numbers, and hyphens"),
  instruments: z.array(z.string()).min(1, "Select at least one instrument"),
  features: z.array(z.string()).min(1, "Add at least one feature"),
  maxStudents: z.number().min(1, "Must allow at least 1 student").max(1000, "Cannot exceed 1000 students"),
  primaryColor: z.string().regex(/^#[0-9A-F]{6}$/i, "Must be a valid hex color"),
  contactEmail: z.string().email("Must be a valid email").optional().or(z.literal("")),
  contactPhone: z.string().optional(),
  website: z.string().url("Must be a valid URL").optional().or(z.literal("")),
  address: z.string().optional(),
  isPublic: z.boolean().default(true),
});

type AcademyCreationFormData = z.infer<typeof academyCreationSchema>;

const instrumentOptions = [
  "Piano", "Keyboard", "Acoustic Guitar", "Electric Guitar", "Bass Guitar",
  "Violin", "Viola", "Cello", "Double Bass", "Flute", "Clarinet", "Saxophone",
  "Trumpet", "Trombone", "French Horn", "Drums", "Percussion", "Voice",
  "Ukulele", "Mandolin", "Banjo", "Harmonica", "Accordion"
];

const defaultFeatures = [
  "Expert Instructors", "Flexible Scheduling", "Small Class Sizes", 
  "Performance Opportunities", "Practice Rooms", "Recording Studio",
  "Music Theory Classes", "Ensemble Playing", "Individual Lessons",
  "Group Classes", "Online Learning", "Student Recitals"
];

interface AcademyCreationFormProps {
  onSuccess?: () => void;
}

export function AcademyCreationForm({ onSuccess }: AcademyCreationFormProps) {
  const [user, setUser] = useState<any>(null);
  const [selectedInstruments, setSelectedInstruments] = useState<string[]>([]);
  const [selectedFeatures, setSelectedFeatures] = useState<string[]>([]);
  const [customFeature, setCustomFeature] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  useEffect(() => {
    const currentUser = getCurrentUser();
    setUser(currentUser);
  }, []);

  const form = useForm<AcademyCreationFormData>({
    resolver: zodResolver(academyCreationSchema),
    defaultValues: {
      academyName: "",
      description: "",
      about: "",
      curriculum: "",
      customSlug: "",
      instruments: [],
      features: [],
      maxStudents: 50,
      primaryColor: "#3B82F6",
      contactEmail: "",
      contactPhone: "",
      website: "",
      address: "",
      isPublic: true,
    },
  });

  // No need for masterId in form since it's handled in submission

  // Update form instruments and features when selected arrays change
  useEffect(() => {
    form.setValue("instruments", selectedInstruments);
  }, [selectedInstruments, form]);

  useEffect(() => {
    form.setValue("features", selectedFeatures);
  }, [selectedFeatures, form]);

  const createAcademyMutation = useMutation({
    mutationFn: (data: AcademyCreationFormData) => 
      apiRequest("/api/classrooms", {
        method: "POST",
        body: JSON.stringify({
          // Required legacy fields for database compatibility
          title: data.academyName,
          subject: selectedInstruments.join(', ') || "Music",
          level: "All Levels",
          description: data.description,
          masterId: parseInt(user?.id?.toString() || "0"),
          maxStudents: data.maxStudents,
          isActive: true,
          // New academy fields (using camelCase for schema validation)
          academyName: data.academyName,
          about: data.about,
          curriculum: data.curriculum,
          customSlug: data.customSlug,
          instruments: selectedInstruments,
          features: selectedFeatures,
          primaryColor: data.primaryColor,
          contactEmail: data.contactEmail || null,
          contactPhone: data.contactPhone || null,
          website: data.website || null,
          address: data.address || null,
          isPublic: data.isPublic,
        }),
      }),
    onSuccess: () => {
      toast({
        title: "Academy Created!",
        description: "Your music academy has been successfully created and is now live.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/classrooms"] });
      form.reset();
      setSelectedInstruments([]);
      setSelectedFeatures([]);
      onSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create academy",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (data: AcademyCreationFormData) => {
    createAcademyMutation.mutate(data);
  };

  const addInstrument = (instrument: string) => {
    if (!selectedInstruments.includes(instrument)) {
      setSelectedInstruments([...selectedInstruments, instrument]);
    }
  };

  const removeInstrument = (instrument: string) => {
    setSelectedInstruments(selectedInstruments.filter(i => i !== instrument));
  };

  const addFeature = (feature: string) => {
    if (!selectedFeatures.includes(feature) && feature.trim()) {
      setSelectedFeatures([...selectedFeatures, feature.trim()]);
    }
  };

  const removeFeature = (feature: string) => {
    setSelectedFeatures(selectedFeatures.filter(f => f !== feature));
  };

  const generateSlug = (name: string) => {
    return name
      .toLowerCase()
      .replace(/[^a-z0-9\s-]/g, '')
      .replace(/\s+/g, '-')
      .replace(/-+/g, '-')
      .trim();
  };

  return (
    <div className="max-w-4xl mx-auto space-y-8">
      <div className="text-center">
        <h2 className="text-3xl font-bold mb-2">Create Your Music Academy</h2>
        <p className="text-muted-foreground">
          Set up your personalized music academy with custom branding and curriculum
        </p>
      </div>

      <Form {...form}>
        <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-8">
          {/* Basic Information */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Music className="h-5 w-5" />
                Basic Information
              </CardTitle>
              <CardDescription>
                Tell us about your music academy
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="academyName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Academy Name</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="e.g., Harmony Music Academy"
                          {...field}
                          onChange={(e) => {
                            field.onChange(e);
                            form.setValue("customSlug", generateSlug(e.target.value));
                          }}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="customSlug"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>URL Slug</FormLabel>
                      <FormControl>
                        <div className="flex items-center">
                          <span className="text-sm text-muted-foreground mr-1">harmonylearn.com/academy/</span>
                          <Input placeholder="harmony-music-academy" {...field} />
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Short Description</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="Brief description for academy listings"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="about"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>About Your Academy</FormLabel>
                    <FormControl>
                      <Textarea 
                        placeholder="Detailed description of your academy, teaching philosophy, and what makes you unique..."
                        rows={4}
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="curriculum"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Curriculum & Teaching Approach</FormLabel>
                    <FormControl>
                      <Textarea 
                        placeholder="Describe your curriculum, teaching methods, and learning progression..."
                        rows={4}
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </CardContent>
          </Card>

          {/* Instruments */}
          <Card>
            <CardHeader>
              <CardTitle>Instruments Taught</CardTitle>
              <CardDescription>
                Select the instruments you teach at your academy
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <Select onValueChange={addInstrument}>
                  <SelectTrigger>
                    <SelectValue placeholder="Add an instrument" />
                  </SelectTrigger>
                  <SelectContent>
                    {instrumentOptions
                      .filter(instrument => !selectedInstruments.includes(instrument))
                      .map((instrument) => (
                      <SelectItem key={instrument} value={instrument}>
                        {instrument}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>

                <div className="flex flex-wrap gap-2">
                  {selectedInstruments.map((instrument) => (
                    <Badge key={instrument} variant="outline" className="text-sm py-2 px-3">
                      {instrument}
                      <Button
                        type="button"
                        variant="ghost"
                        size="sm"
                        className="ml-2 h-auto p-0"
                        onClick={() => removeInstrument(instrument)}
                      >
                        <X className="h-3 w-3" />
                      </Button>
                    </Badge>
                  ))}
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Features */}
          <Card>
            <CardHeader>
              <CardTitle>Academy Features</CardTitle>
              <CardDescription>
                Highlight what makes your academy special
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
                  {defaultFeatures
                    .filter(feature => !selectedFeatures.includes(feature))
                    .map((feature) => (
                    <Button
                      key={feature}
                      type="button"
                      variant="outline"
                      size="sm"
                      onClick={() => addFeature(feature)}
                      className="justify-start"
                    >
                      <Plus className="h-3 w-3 mr-1" />
                      {feature}
                    </Button>
                  ))}
                </div>

                <div className="flex gap-2">
                  <Input
                    placeholder="Add custom feature..."
                    value={customFeature}
                    onChange={(e) => setCustomFeature(e.target.value)}
                    onKeyPress={(e) => {
                      if (e.key === 'Enter') {
                        e.preventDefault();
                        addFeature(customFeature);
                        setCustomFeature("");
                      }
                    }}
                  />
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => {
                      addFeature(customFeature);
                      setCustomFeature("");
                    }}
                  >
                    Add
                  </Button>
                </div>

                <div className="flex flex-wrap gap-2">
                  {selectedFeatures.map((feature) => (
                    <Badge key={feature} className="text-sm py-2 px-3">
                      {feature}
                      <Button
                        type="button"
                        variant="ghost"
                        size="sm"
                        className="ml-2 h-auto p-0 text-primary-foreground"
                        onClick={() => removeFeature(feature)}
                      >
                        <X className="h-3 w-3" />
                      </Button>
                    </Badge>
                  ))}
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Settings */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Settings className="h-5 w-5" />
                Academy Settings
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="maxStudents"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Maximum Students</FormLabel>
                      <FormControl>
                        <Input 
                          type="number"
                          {...field}
                          onChange={(e) => field.onChange(parseInt(e.target.value) || 0)}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="primaryColor"
                  render={({ field }) => (
                    <FormItem>
