import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { getCurrentUser } from "@/lib/auth";
import { Users, BookOpen, Globe } from "lucide-react";

const apiRequest = async (url: string, options: { method: string; body?: string }) => {
  const response = await fetch(url, {
    method: options.method,
    headers: {
      'Content-Type': 'application/json',
    },
    body: options.body,
  });

  if (!response.ok) {
    const errorData = await response.json().catch(() => ({}));
    throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
  }

  return response.json();
};

const classroomSchema = z.object({
  title: z.string().min(3, "Title must be at least 3 characters").max(100, "Title too long"),
  description: z.string().min(10, "Description must be at least 10 characters").max(500, "Description too long"),
  subject: z.string().min(1, "Subject is required"),
  level: z.string().min(1, "Level is required"),
  maxStudents: z.number().min(1, "Must allow at least 1 student").max(500, "Too many students"),
});

type ClassroomFormData = z.infer<typeof classroomSchema>;

interface ClassroomCreationFormProps {
  onSuccess?: () => void;
  onCancel?: () => void;
}

const subjects = [
  "Piano",
  "Guitar", 
  "Violin",
  "Vocals",
  "Drums",
  "Bass",
  "Music Theory",
  "Composition",
  "Music Production",
  "General Music"
];

const levels = [
  "Beginner",
  "Intermediate", 
  "Advanced",
  "All Levels"
];

export default function ClassroomCreationForm({ onSuccess, onCancel }: ClassroomCreationFormProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const currentUser = getCurrentUser();

  const form = useForm<ClassroomFormData>({
    resolver: zodResolver(classroomSchema),
    defaultValues: {
      title: "",
      description: "",
      subject: "",
      level: "",
      maxStudents: 30,
    },
  });

  const createClassroom = useMutation({
    mutationFn: async (data: ClassroomFormData) => {
      return apiRequest('/api/classrooms', {
        method: 'POST',
        body: JSON.stringify({
          ...data,
          masterId: parseInt(currentUser?.id || '0'),
        }),
      });
    },
    onSuccess: () => {
      toast({
        title: "Classroom Created",
        description: "Your classroom has been created successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/classrooms'] });
      form.reset();
      onSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create classroom",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ClassroomFormData) => {
    createClassroom.mutate(data);
  };

  return (
    <Card className="max-w-2xl mx-auto">
      <CardHeader className="text-center space-y-4">
        <div className="mx-auto w-16 h-16 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center">
          <Users className="w-8 h-8 text-white" />
        </div>
        <CardTitle className="text-2xl font-bold">Create New Classroom</CardTitle>
        <CardDescription className="text-lg">
          Set up a new classroom to teach and manage your students
        </CardDescription>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Feature highlights */}
        <div className="grid md:grid-cols-3 gap-4 mb-6">
          <div className="text-center p-3 bg-blue-50 dark:bg-blue-950/20 rounded-lg">
            <BookOpen className="w-6 h-6 text-blue-600 mx-auto mb-1" />
            <h4 className="font-medium text-blue-800 dark:text-blue-300 text-sm">Course Management</h4>
          </div>
          <div className="text-center p-3 bg-green-50 dark:bg-green-950/20 rounded-lg">
            <Users className="w-6 h-6 text-green-600 mx-auto mb-1" />
            <h4 className="font-medium text-green-800 dark:text-green-300 text-sm">Student Tracking</h4>
          </div>
          <div className="text-center p-3 bg-purple-50 dark:bg-purple-950/20 rounded-lg">
            <Globe className="w-6 h-6 text-purple-600 mx-auto mb-1" />
            <h4 className="font-medium text-purple-800 dark:text-purple-300 text-sm">Live Sessions</h4>
          </div>
        </div>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <FormField
              control={form.control}
              name="title"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-base font-semibold">Classroom Title *</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="e.g., Advanced Piano Techniques, Guitar Fundamentals..."
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-base font-semibold">Description *</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Describe what students will learn in this classroom, teaching methods, expectations..."
                      className="min-h-[100px] resize-none"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid md:grid-cols-2 gap-6">
              <FormField
                control={form.control}
                name="subject"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-base font-semibold">Subject *</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder="Select subject" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {subjects.map((subject) => (
                          <SelectItem key={subject} value={subject}>
                            {subject}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="level"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-base font-semibold">Level *</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder="Select level" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {levels.map((level) => (
                          <SelectItem key={level} value={level}>
                            {level}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="maxStudents"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-base font-semibold">Maximum Students</FormLabel>
                  <FormControl>
                    <Input
                      type="number"
                      min={1}
                      max={500}
                      {...field}
                      onChange={(e) => field.onChange(parseInt(e.target.value) || 1)}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-4 pt-4">
              {onCancel && (
                <Button
                  type="button"
                  variant="outline"
                  onClick={onCancel}
                  className="flex-1"
                >
                  Cancel
                </Button>
              )}
              <Button
                type="submit"
                disabled={createClassroom.isPending}
                className="flex-1 bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700"
              >
                {createClassroom.isPending ? "Creating..." : "Create Classroom"}
              </Button>
            </div>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

client/src/components/master/EnrollmentR.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { 
  Clock, CheckCircle, XCircle, Search, Mail, Calendar,
  UserCheck, UserX, AlertCircle, Users, Eye
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { getCurrentUser } from "@/lib/auth";

interface EnrollmentRequest {
  id: number;
  userId: number;
  classroomId: number;
  role: string;
  status: string;
  joinedAt: string;
  username: string;
  firstName: string;
  lastName: string;
  email: string;
  avatar: string;
}

interface EnrollmentRequestManagerProps {
  classroomId: number;
  academyName: string;
}

export default function EnrollmentRequestManager({ classroomId, academyName }: EnrollmentRequestManagerProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedTab, setSelectedTab] = useState("pending");
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const user = getCurrentUser();

  // Fetch enrollment requests
  const { data: allRequests = [], isLoading } = useQuery({
    queryKey: ["/api/classrooms", classroomId, "requests"],
    queryFn: () => apiRequest(`/api/classrooms/${classroomId}/requests`),
    enabled: !!classroomId,
  });

  // Filter requests by status and search
  const pendingRequests = allRequests.filter((req: EnrollmentRequest) => req.status === 'pending');
  const approvedRequests = allRequests.filter((req: EnrollmentRequest) => req.status === 'active');
  const rejectedRequests = allRequests.filter((req: EnrollmentRequest) => req.status === 'rejected');

  const filteredRequests = {
    pending: pendingRequests.filter((req: EnrollmentRequest) => 
      req.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      req.firstName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      req.lastName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      req.email?.toLowerCase().includes(searchTerm.toLowerCase())
    ),
    approved: approvedRequests.filter((req: EnrollmentRequest) => 
      req.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      req.firstName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      req.lastName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      req.email?.toLowerCase().includes(searchTerm.toLowerCase())
    ),
    rejected: rejectedRequests.filter((req: EnrollmentRequest) => 
      req.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      req.firstName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      req.lastName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      req.email?.toLowerCase().includes(searchTerm.toLowerCase())
    )
  };

  // Approve request mutation
  const approveRequestMutation = useMutation({
    mutationFn: (requestId: number) => 
      apiRequest(`/api/classroom-memberships/${requestId}/status`, {
        method: "PATCH",
        body: JSON.stringify({ 
          status: "active",
          reviewedBy: user?.id 
        })
      }),
    onSuccess: () => {
      toast({ 
        title: "Request Approved!", 
        description: "The student has been accepted into your academy."
      });
      queryClient.invalidateQueries({ queryKey: ["/api/classrooms", classroomId, "requests"] });
    },
    onError: () => {
      toast({ 
        title: "Failed to approve request", 
        description: "There was an error approving the request. Please try again.",
        variant: "destructive" 
      });
    }
  });

  // Reject request mutation
  const rejectRequestMutation = useMutation({
    mutationFn: (requestId: number) => 
      apiRequest(`/api/classroom-memberships/${requestId}/status`, {
        method: "PATCH",
        body: JSON.stringify({ 
          status: "rejected",
          reviewedBy: user?.id 
        })
      }),
    onSuccess: () => {
      toast({ 
        title: "Request Rejected", 
        description: "The enrollment request has been declined."
      });
      queryClient.invalidateQueries({ queryKey: ["/api/classrooms", classroomId, "requests"] });
    },
    onError: () => {
      toast({ 
        title: "Failed to reject request", 
        description: "There was an error rejecting the request. Please try again.",
        variant: "destructive" 
      });
    }
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'active': return 'bg-green-100 text-green-800';
      case 'rejected': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending': return <Clock className="h-4 w-4" />;
      case 'active': return <CheckCircle className="h-4 w-4" />;
      case 'rejected': return <XCircle className="h-4 w-4" />;
      default: return <AlertCircle className="h-4 w-4" />;
    }
  };

  const renderRequestsTable = (requests: EnrollmentRequest[], showActions = false) => (
    <Table>
      <TableHeader>
        <TableRow>
          <TableHead>Student</TableHead>
          <TableHead>Contact</TableHead>
          <TableHead>Request Date</TableHead>
          <TableHead>Status</TableHead>
          {showActions && <TableHead>Actions</TableHead>}
        </TableRow>
      </TableHeader>
      <TableBody>
        {requests.length === 0 ? (
          <TableRow>
            <TableCell colSpan={showActions ? 5 : 4} className="text-center py-8 text-muted-foreground">
              No {selectedTab} requests found
            </TableCell>
          </TableRow>
        ) : (
          requests.map((request) => (
            <TableRow key={request.id}>
              <TableCell>
                <div className="flex items-center gap-3">
                  <Avatar className="h-8 w-8">
                    <AvatarImage src={request.avatar} />
                    <AvatarFallback>
                      {request.firstName?.[0]}{request.lastName?.[0]}
                    </AvatarFallback>
                  </Avatar>
                  <div>
                    <div className="font-medium">
                      {request.firstName && request.lastName ? 
                        `${request.firstName} ${request.lastName}` : 
                        request.username}
                    </div>
                    <div className="text-sm text-muted-foreground">@{request.username}</div>
                  </div>
                </div>
              </TableCell>
              <TableCell>
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <Mail className="h-4 w-4" />
                  {request.email}
                </div>
              </TableCell>
              <TableCell>
                <div className="flex items-center gap-2 text-sm">
                  <Calendar className="h-4 w-4 text-muted-foreground" />
                  {new Date(request.joinedAt).toLocaleDateString()}
                </div>
              </TableCell>
              <TableCell>
                <Badge className={getStatusColor(request.status)}>
                  {getStatusIcon(request.status)}
                  <span className="ml-1 capitalize">{request.status}</span>
                </Badge>
              </TableCell>
              {showActions && (
                <TableCell>
                  <div className="flex gap-2">
                    <Button
                      size="sm"
                      onClick={() => approveRequestMutation.mutate(request.id)}
                      disabled={approveRequestMutation.isPending}
                      className="bg-green-600 hover:bg-green-700"
                    >
                      <UserCheck className="h-4 w-4 mr-1" />
                      Approve
                    </Button>
                    <Button
                      size="sm"
                      variant="destructive"
                      onClick={() => rejectRequestMutation.mutate(request.id)}
                      disabled={rejectRequestMutation.isPending}
                    >
                      <UserX className="h-4 w-4 mr-1" />
                      Reject
                    </Button>
                  </div>
                </TableCell>
              )}
            </TableRow>
          ))
        )}
      </TableBody>
    </Table>
  );

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
            <p>Loading enrollment requests...</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Users className="h-5 w-5" />
          Enrollment Requests
        </CardTitle>
        <CardDescription>
          Manage student enrollment requests for {academyName}
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Search */}
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Search by name, username, or email..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10"
          />
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center gap-2">
                <Clock className="h-5 w-5 text-yellow-500" />
                <div>
                  <div className="text-2xl font-bold">{pendingRequests.length}</div>
                  <div className="text-sm text-muted-foreground">Pending</div>
                </div>
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center gap-2">
                <CheckCircle className="h-5 w-5 text-green-500" />
                <div>
                  <div className="text-2xl font-bold">{approvedRequests.length}</div>
                  <div className="text-sm text-muted-foreground">Approved</div>
                </div>
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center gap-2">
                <XCircle className="h-5 w-5 text-red-500" />
                <div>
                  <div className="text-2xl font-bold">{rejectedRequests.length}</div>
                  <div className="text-sm text-muted-foreground">Rejected</div>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Tabs */}
        <Tabs value={selectedTab} onValueChange={setSelectedTab}>
          <TabsList>
            <TabsTrigger value="pending" className="flex items-center gap-2">
              <Clock className="h-4 w-4" />
              Pending ({pendingRequests.length})
            </TabsTrigger>
            <TabsTrigger value="approved" className="flex items-center gap-2">
              <CheckCircle className="h-4 w-4" />
              Approved ({approvedRequests.length})
            </TabsTrigger>
            <TabsTrigger value="rejected" className="flex items-center gap-2">
              <XCircle className="h-4 w-4" />
              Rejected ({rejectedRequests.length})
            </TabsTrigger>
          </TabsList>

          <TabsContent value="pending" className="mt-6">
            {renderRequestsTable(filteredRequests.pending, true)}
          </TabsContent>

          <TabsContent value="approved" className="mt-6">
            {renderRequestsTable(filteredRequests.approved, false)}
          </TabsContent>

          <TabsContent value="rejected" className="mt-6">
            {renderRequestsTable(filteredRequests.rejected, false)}
          </TabsContent>
        </Tabs>
      </CardContent>
    </Card>
  );
}

client/src/components/mentor/MasterRoleR.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { getCurrentUser } from "@/lib/auth";
import { Crown, Users, BookOpen, Award } from "lucide-react";

const apiRequest = async (url: string, options: { method: string; body?: string }) => {
  const response = await fetch(url, {
    method: options.method,
    headers: {
      'Content-Type': 'application/json',
    },
    body: options.body,
  });

  if (!response.ok) {
    const errorData = await response.json().catch(() => ({}));
    throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
  }

  return response.json();
};

const masterRoleRequestSchema = z.object({
  reason: z.string().min(50, "Please provide at least 50 characters explaining why you want to become a master"),
  experience: z.string().min(30, "Please describe your teaching/classroom management experience (minimum 30 characters)"),
  plannedClassrooms: z.string().min(30, "Please describe your planned classrooms (minimum 30 characters)"),
  additionalQualifications: z.string().optional(),
});

type MasterRoleRequestFormData = z.infer<typeof masterRoleRequestSchema>;

interface MasterRoleRequestFormProps {
  onSuccess?: () => void;
}

export default function MasterRoleRequestForm({ onSuccess }: MasterRoleRequestFormProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const currentUser = getCurrentUser();

  const form = useForm<MasterRoleRequestFormData>({
    resolver: zodResolver(masterRoleRequestSchema),
    defaultValues: {
      reason: "",
      experience: "",
      plannedClassrooms: "",
      additionalQualifications: "",
    },
  });

  const submitRequest = useMutation({
    mutationFn: async (data: MasterRoleRequestFormData) => {
      return apiRequest(`/api/master-role-requests`, {
        method: "POST",
        body: JSON.stringify({
          ...data,
          mentorId: parseInt(currentUser?.id || "0"),
        }),
      });
    },
    onSuccess: () => {
      toast({
        title: "Master Role Request Submitted",
        description: "Your request has been submitted successfully. You'll be notified when it's reviewed.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/master-role-requests"] });
      form.reset();
      onSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to submit master role request",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: MasterRoleRequestFormData) => {
    submitRequest.mutate(data);
  };

  return (
    <Card className="max-w-4xl mx-auto">
      <CardHeader className="text-center space-y-4">
        <div className="mx-auto w-16 h-16 bg-gradient-to-br from-yellow-400 to-orange-500 rounded-full flex items-center justify-center">
          <Crown className="w-8 h-8 text-white" />
        </div>
        <CardTitle className="text-2xl font-bold">Request Master Role</CardTitle>
        <CardDescription className="text-lg">
          Apply to become a master and unlock classroom creation and management features
        </CardDescription>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Benefits Section */}
        <div className="grid md:grid-cols-3 gap-4 mb-8">
          <div className="text-center p-4 bg-blue-50 dark:bg-blue-950/20 rounded-lg">
            <Users className="w-8 h-8 text-blue-600 mx-auto mb-2" />
            <h3 className="font-semibold text-blue-800 dark:text-blue-300">Create Classrooms</h3>
            <p className="text-sm text-blue-600 dark:text-blue-400">Build and manage your own virtual classrooms</p>
          </div>
          <div className="text-center p-4 bg-green-50 dark:bg-green-950/20 rounded-lg">
            <BookOpen className="w-8 h-8 text-green-600 mx-auto mb-2" />
            <h3 className="font-semibold text-green-800 dark:text-green-300">Advanced Tools</h3>
            <p className="text-sm text-green-600 dark:text-green-400">Access advanced teaching and management tools</p>
          </div>
          <div className="text-center p-4 bg-purple-50 dark:bg-purple-950/20 rounded-lg">
            <Award className="w-8 h-8 text-purple-600 mx-auto mb-2" />
            <h3 className="font-semibold text-purple-800 dark:text-purple-300">Enhanced Status</h3>
            <p className="text-sm text-purple-600 dark:text-purple-400">Gain recognition as a master educator</p>
          </div>
        </div>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <FormField
              control={form.control}
              name="reason"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-base font-semibold">
                    Why do you want to become a master? *
                  </FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Explain your motivation for becoming a master, your goals for classroom management, and how it aligns with your teaching philosophy..."
                      className="min-h-[120px] resize-none"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="experience"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-base font-semibold">
                    Teaching & Classroom Management Experience *
                  </FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Describe your experience in teaching, managing students, curriculum development, or any relevant educational background..."
                      className="min-h-[120px] resize-none"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="plannedClassrooms"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-base font-semibold">
                    Planned Classrooms & Teaching Approach *
                  </FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Describe the type of classrooms you plan to create, your teaching methodology, subject areas, and how you'll engage students..."
